typedef struct tubestruct{
	uint16_t posX;
	uint16_t posY;
	uint16_t Height;
} Tube;

uint16_t toad_posX;
uint16_t toad_posY;
uint8_t toad_SpeedY;

void Game_Init()
{
	toad_posX = 10;
	toad_posY = 64;
}

void Draw_Toad()
{
	uint8_t i,j;
	for (i = 0; i < 32; i++)
	{
		for (j = 0; j < 40; j++)
		{
			if(toadImage[i][j])
			LCD_Fast_DrawPoint(toad_posY + i, toad_posX + j, toadImage[i][j]);
		}
	}
}

void Erase_LastToad()
{
	uint8_t i, j;
	for (i = 0; i < 32; i++)
	{
		for (j = 0; j < 40; j++)
		{
			LCD_Fast_DrawPoint(toad_posY + i, toad_posX + j, BACK_COLOR);
		}
	}
}

void Toad_Go(void)
{
	toad_SpeedY+=3;
	Erase_LastToad();
	toad_posY += toad_SpeedY;
	Draw_Toad();
}
